//
// GetShiftSchedules service
// * startTime - DATETIME
// * endTime - DATETIME
// * productionLine - THINGNAME

// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(BayerShiftSchedulesDataShape)
var result = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    dataShapeName: "BayerShiftSchedulesDataShape"
});

// result: INFOTABLE dataShape: "BayerShiftScheduleItemDataShape"
var items = Things[me.GetConnector()].GetShiftsScheduleByTime({
    productionLine: productionLine /* THINGNAME */,
    startTime: startTime /* DATETIME */,
    endTime: endTime /* DATETIME */
});

var lastShiftId = null;
var shiftEntry = null;

var tableLength = items.rows.length;
for (var idx = 0; idx < tableLength; idx++) {
    var row = items.rows[idx];

    if (row.id !== lastShiftId) {
        lastShiftId = row.id;
        if (shiftEntry) {
            result.AddRow(shiftEntry);
        }
        shiftEntry = {};
        shiftEntry.shiftSchedule = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
            dataShapeName: "BayerScheduleDataShape"
        }); // INFOTABLE
        shiftEntry.totalQuantityProduced = row.totalQuantityProduced; // NUMBER
        shiftEntry.totalTargetQuantity = row.totalTargetQuantity; // NUMBER
        shiftEntry.id = row.id;
        shiftEntry.timestamp = row.timestamp;
        shiftEntry.source = row.productionLine;
    }
    // BayerScheduleDataShape entry object
    var newEntry = {};
    newEntry.duration = row.duration; // INTEGER
    newEntry.productionPlanId = row.productionPlanId; // INTEGER
    newEntry.productionPlanId_actual = row.productionPlanId_actual; // INTEGER
    newEntry.hour = row.hour; // INTEGER
    newEntry.productId = row.productId; // STRING
    newEntry.quantityTargetOk = row.quantityTargetOk; // BOOLEAN
    newEntry.time = row.time; // DATETIME
    newEntry.quantityProduced = row.quantityProduced; // NUMBER
    newEntry.quantityProducedBlisters = row.quantityProducedBlisters;
    newEntry.productDescription = row.productDescription; // STRING
    newEntry.targetQuantity = row.targetQuantity; // NUMBER
    newEntry.targetQuantityBlisters = row.targetQuantityBlisters;
    newEntry.performance = newEntry.targetQuantity / newEntry.quantityProduced * 100.0;
    if (isNaN(newEntry.performance) || !isFinite(newEntry.performance) || !newEntry.quantityProduced)
        newEntry.performance = 0;
    else
        newEntry.performance = newEntry.performance.toFixed(0);
    shiftEntry.shiftSchedule.AddRow(newEntry);
} //# for each shifts schedule entry
if (shiftEntry) {
    result.AddRow(shiftEntry);
}


// result: INFOTABLE dataShape: "BayerShiftScheduleDataShape"
