var tableLength = items.rows.length;
for (var x=0; x < tableLength; x++) {
    var row = items.rows[x];

    var params = {};
    // infotable datashape iteration
    var dataShapeFields = items.dataShape.fields;
    for (var fieldName in dataShapeFields) {
        params[fieldName] = row[fieldName];
    }

    var oldId = undefined;
    if(!!row.id) {
        oldId = row.id;
    }
    var insertedId =  Things[me.GetConnector()].InsertProductionPlan(params);
	result.AddRow({ id: insertedId.rows[0].id, oldId: oldId });
} // for each input row


